import React from 'react';

interface ShoeItemProps {
  shoe: {
    _id: string;
    name: string;
    price: number;
    thumbnail: string; // Added thumbnail
  };
  addToCart: (shoeId: string) => void;
}

const ShoeItem: React.FC<ShoeItemProps> = ({ shoe, addToCart }) => {
  return (
    <div className="shoe-item">
      <img src={shoe.thumbnail} alt={shoe.name} className="shoe-image" />
      <h3>{shoe.name}</h3>
      <p>Price: ${shoe.price}</p>
      <button onClick={() => addToCart(shoe._id)} className="add-to-cart-button">Add to Cart</button>
    </div>
  );
};

export default ShoeItem;
