import React, { useEffect, useState } from 'react';
import ShoeItem from '../components/ShoeItem';
import Navbar from '../components/Navbar';
import { getShoes, addToCart } from '../utils/api';
import './HomePage.css'; // Import the CSS file for styles

const HomePage: React.FC = () => {
  const [shoes, setShoes] = useState([]);

  useEffect(() => {
    getShoes().then((data) => setShoes(data));
  }, []);

  return (
    <div className="home-page">
      <Navbar />
      <h1 className="home-title">Shoes Collection</h1>
      <div className="shoe-list">
        {shoes.length > 0 ? (
          shoes.map((shoe: any) => (
            <ShoeItem key={shoe._id} shoe={shoe} addToCart={addToCart} />
          ))
        ) : (
          <p className="no-shoes-message">No shoes available.</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
